@AbapCatalog.viewEnhancementCategory: [#NONE]
@AccessControl.authorizationCheck: #NOT_REQUIRED
@EndUserText.label: 'Material Due Date List'
@Metadata.ignorePropagatedAnnotations: true
@ObjectModel.usageType:{
    serviceQuality: #X, 
    sizeCategory: #S,
    dataClass: #MIXED
}
@Metadata.allowExtensions: true
define root view entity ZYCDS_DUE_MAT_LIST2 as select from ZYCDS_DUE_MAT_LIST
association [1..1] to I_StorageLocation on I_StorageLocation.StorageLocation = ZYCDS_DUE_MAT_LIST.StorageLocation
                                       and I_StorageLocation.Plant           = ZYCDS_DUE_MAT_LIST.Plant
 association [0..1] to zydc_config_cds_poc as _Config on ZYCDS_DUE_MAT_LIST.Remaining_days >= _Config.daysfrom  
                               and ZYCDS_DUE_MAT_LIST.Remaining_days <= _Config.daysto                            
{
  key ZYCDS_DUE_MAT_LIST.Material,
  key ZYCDS_DUE_MAT_LIST.Plant,
  key ZYCDS_DUE_MAT_LIST.StorageLocation,
  key ZYCDS_DUE_MAT_LIST.Batch,
  key ZYCDS_DUE_MAT_LIST.Supplier,
//  key YCDS_DUE_MAT_LIST.InventoryStockType,
//  key YCDS_DUE_MAT_LIST.MatlDocLatestPostgDate,
  ZYCDS_DUE_MAT_LIST.ProductDescription,
  ZYCDS_DUE_MAT_LIST.PlantName,
  I_StorageLocation.StorageLocationName,
  ZYCDS_DUE_MAT_LIST.BatchBySupplier,
  ZYCDS_DUE_MAT_LIST.SupplierName,
//  MatlWrhsStkQtyInMatlBaseUnit,
  ZYCDS_DUE_MAT_LIST.BaseUnit, 
  @Semantics.quantity.unitOfMeasure: 'BaseUnit'
  MatlWrhsStkQtyInMatlBaseUnit,
  MatlBatchIsInRstrcdUseStock,
  ZYCDS_DUE_MAT_LIST.ShelfLifeExpirationDate,
  ZYCDS_DUE_MAT_LIST.ManufactureDate,
  ZYCDS_DUE_MAT_LIST.BatchStatus,
  Remaining_days, 
     _Config.criticality as Criticality,
  _Config.remarks as Due_range
    
    
//     cast( '0' as abap.int1 ) as Criticality
}  where _Config.remarks != '0'
